import{_ as e,c as t,a0 as o,o as r}from"./chunks/framework.DzSQaq9h.js";const l="/pbottleRPA/assets/eb678332-8356-478e-b00d-600e6afdbc8c.Bzkw80iW.jpg",b=JSON.parse('{"title":"API统一规范定义","description":"","frontmatter":{},"headers":[],"relativePath":"API统一规范.md","filePath":"API统一规范.md","lastUpdated":1742369216000}'),s={name:"API统一规范.md"};function n(i,a,p,d,h,c){return r(),t("div",null,a[0]||(a[0]=[o('<h1 id="api统一规范定义" tabindex="-1">API统一规范定义 <a class="header-anchor" href="#api统一规范定义" aria-label="Permalink to &quot;API统一规范定义&quot;">​</a></h1><h2 id="屏幕坐标" tabindex="-1">屏幕坐标 <a class="header-anchor" href="#屏幕坐标" aria-label="Permalink to &quot;屏幕坐标&quot;">​</a></h2><p>以屏幕左上角为原点（坐标为 (0, 0)），水平向右为 x 轴正方向，垂直向下为 y 轴正方向。在这种坐标系中，屏幕上任意一点都可以通过一个 (x, y) 的坐标值来表示，其中 x 表示该点距离原点在水平方向的距离，y 表示在垂直方向的距离，单位通常是像素。</p><p>注意：在有缩放的屏幕上时，其中的像素单位是<strong>物理像素</strong>，不是软件像素。</p><p><img src="'+l+'" alt="小瓶RPA屏幕坐标图片示意图"></p><h4 id="推荐截图和测量工具" tabindex="-1">推荐截图和测量工具 <a class="header-anchor" href="#推荐截图和测量工具" aria-label="Permalink to &quot;推荐截图和测量工具&quot;">​</a></h4><p>snipaste 截图软件自带坐标测量。</p><p>下载地址： <a href="https://zh.snipaste.com/download.html" target="_blank" rel="noreferrer">https://zh.snipaste.com/download.html</a></p><h4 id="分辨率测量工具" tabindex="-1">分辨率测量工具 <a class="header-anchor" href="#分辨率测量工具" aria-label="Permalink to &quot;分辨率测量工具&quot;">​</a></h4><p><a href="https://www.pbottle.com/a-13812.html" target="_blank" rel="noreferrer">https://www.pbottle.com/a-13812.html</a></p><h2 id="统一路径格式" tabindex="-1">统一路径格式 <a class="header-anchor" href="#统一路径格式" aria-label="Permalink to &quot;统一路径格式&quot;">​</a></h2><p>小瓶RPA脚本默认路径格式分隔符为 <code>/</code>，包含 windows 系统。</p><p>windows 系统路径规则统一到 Linux 系统路径规则。</p><p>举例：</p><ol><li>绝对路径 <code>D:/myPath/test.png</code></li><li>相对路径 <code>./test.png</code></li></ol>',15)]))}const f=e(s,[["render",n]]);export{b as __pageData,f as default};
